import { MOVIES } from "../constants";
import movieData from "../movieData.json";

const movieReducer = (state = movieData, action) => {
  if (action.type === MOVIES.LOAD_SUCCESS) {
    return [...state, ...action.data];
  }
  return state;
};

export default movieReducer;
